import { XIcon } from "@phosphor-icons/react/dist/ssr/X";
import { cva, type VariantProps } from "class-variance-authority";
import { Button } from "@/components/button";
import { cn } from "@/lib/utils";

const alertVariants = cva(
  "relative grid w-full grid-cols-[0_1fr] items-start gap-y-0.5 rounded-lg border px-4 py-3 text-sm has-[>svg]:grid-cols-[calc(var(--spacing)*4)_1fr] has-[>svg]:gap-x-3 [&>svg]:size-4 [&>svg]:translate-y-0.5 [&>svg]:text-current",
  {
    variants: {
      variant: {
        default: "bg-card text-card-foreground",
        info: "bg-card text-info-foreground *:data-[slot=alert-description]:text-info-foreground/90",
        warning:
          "bg-card text-warning-foreground *:data-[slot=alert-description]:text-warning-foreground/90",
        success:
          "bg-card text-success-foreground *:data-[slot=alert-description]:text-success-foreground/90",
        danger:
          "bg-card text-danger *:data-[slot=alert-description]:text-danger/90",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

function Alert({
  className,
  variant,
  ...props
}: React.ComponentProps<"div"> & VariantProps<typeof alertVariants>) {
  return (
    <div
      data-slot="alert"
      role="alert"
      className={cn(alertVariants({ variant }), className)}
      {...props}
    />
  );
}

function AlertTitle({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="alert-title"
      className={cn(
        "col-start-2 line-clamp-1 min-h-4 font-medium tracking-tight",
        className,
      )}
      {...props}
    />
  );
}

function AlertDescription({
  className,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="alert-description"
      className={cn(
        "col-start-2 grid justify-items-start gap-1 text-sm text-muted-foreground [&_p]:leading-relaxed",
        className,
      )}
      {...props}
    />
  );
}

function AlertClose() {
  return (
    <Button
      data-slot="alert-close"
      variant="ghost"
      size="xs"
      className="absolute top-2 right-2 rounded-md"
    >
      <XIcon />
    </Button>
  );
}

export { Alert, AlertTitle, AlertDescription, AlertClose };
